package list.collection;

import java.util.Hashtable;

public class HashTableDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Hashtable ht  =new Hashtable();
		ht.put(10, "harsh");
		ht.put(true, 100);
		ht.put("tree",false);
		ht.put(10.3,50);
		System.out.println(ht);
	}

}
